contract Exchange  is  ERC2771Context, AccessControl, Pausable {
    IUniswapV2Router02 public uniswapRouter;
    IERC20 public USDToken;
    FeeManager public feeManager;

    StableManager public stableManager;


     constructor(address trustedForwarder, address _feeManager) ERC2771Context(address(trustedForwarder)) {
        uniswapRouter = IUniswapV2Router02(0xE592427A0AEce92De3Edee1F18E0157C05861564);
        USDToken = IERC20(0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48);

        feeManager = FeeManager(_feeManager);
    }

     function _msgSender() internal view override(ERC2771Context, Context) returns (address sender) {
        return ERC2771Context._msgSender();
    }

    function _msgData() internal view override(Context, ERC2771Context) returns (bytes calldata) {
        return super._msgData();
    }


    // function swapStableToToken(address stableToken,address token,  uint256 amount, address partner) public  returns(bool){
    //     require(stableToken != address(0), "stableToken can't be null");
    //     require(amount > 0, "Amount need to be greater than zero");

    //     ERC20Stable stableCoin = ERC20Stable(stableToken);


    //     require(stableCoin.balanceOf(_msgSender()) >= amount, "Token not enough");
    //     require(stableCoin.allowance(_msgSender(), address(this)) >= amount, "Allowance not enough");



    //     stableCoin.transferFrom(_msgSender(), address(this),  amount);

    //     stableCoin.approve(address(stableManager), amount);
    //     stableManager.burnStable(stableToken, amount, partner);


    //     uint256 usdBalance = USDToken.balanceOf(address(this));

    //     USDToken.approve(address(uniswapRouter), usdBalance);

    //     address[] memory path = new address[](2);
    //     path[0] = address(USDToken);
    //     path[1] = address(token);

    //     //Swap the USD to token
    //     uniswapRouter.swapExactTokensForTokens(
    //     usdBalance,
    //     0,
    //     path,
    //     _msgSender(),
    //     block.timestamp
    //     );

    //     return true;
    // }

    function swapStableToToken(address stableToken,address token,  uint256 amount, address partner) public  returns(bool){
        require(stableToken != address(0), "stableToken can't be null");
        require(amount > 0, "Amount need to be greater than zero");

        ERC20Stable stableCoin = ERC20Stable(stableToken);


        require(stableCoin.balanceOf(_msgSender()) >= amount, "Token not enough");
        require(stableCoin.allowance(_msgSender(), address(this)) >= amount, "Allowance not enough");



        stableCoin.transferFrom(_msgSender(), address(this),  amount);

        stableCoin.approve(address(stableManager), amount);
        stableManager.burnStable(stableToken, amount, partner);


        uint256 usdBalance = USDToken.balanceOf(address(this));

        USDToken.approve(address(uniswapRouter), usdBalance);

        address[] memory path = new address[](2);
        path[0] = address(USDToken);
        path[1] = address(token);

        //Swap the USD to token
        uniswapRouter.swapExactTokensForTokens(
        usdBalance,
        0,
        path,
        _msgSender(),
        block.timestamp
        );

        return true;
    }

    function swapTokentoStable( address token, address stableToken,  uint256 amount, address partner) public  returns(bool){
        require(token != address(0), "stableToken can't be null");
        require(amount > 0, "Amount need to be greater than zero");

        ERC20 tokenErc = ERC20(token);
   ERC20Stable stableCoin = ERC20Stable(stableToken);


        require(tokenErc.balanceOf(_msgSender()) >= amount, "Token not enough");
        require(tokenErc.allowance(_msgSender(), address(this)) >= amount, "Allowance not enough");



        tokenErc.transferFrom(_msgSender(), address(this),  amount);

        // stableCoin.approve(address(stableManager), amount);
        // stableManager.burnStable(stableToken, amount, partner);


        uint256 tokenBalance = tokenErc.balanceOf(address(this));

        tokenErc.approve(address(uniswapRouter), tokenBalance);

        address[] memory path = new address[](2);
        path[0] = address(token);
        path[1] = address(USDToken);


        //Swap the USD to token
      uint256[] memory usdBalances =  uniswapRouter.swapExactTokensForTokens(
        tokenBalance,
        0,
        path,
        _msgSender(),
        block.timestamp
        );


        USDToken.approve(address(stableManager), usdBalances[1]);
        uint256  amount_ =  stableManager.mintStable(stableToken, usdBalances[1], partner);

        stableCoin.transfer(_msgSender(), amount_);


        return true;
    }
}

contracts/FeeManager.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";


import {
    ERC2771Context
} from "@gelatonetwork/relay-context/contracts/vendor/ERC2771Context.sol";


contract FeeManager is ERC2771Context, AccessControl {
     using Math for uint256;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

     enum Service {
        MINT,    //0
        BURN,    //1
        TRANSFER,//2
        FOREX,   //3
        DEX,     //4
        LEND     //5
    }

    struct PartnersFee {
        address wallet;
        uint256 fee;
    }

    mapping(Service => uint256) public fees;

    PartnersFee[] public partnersFees;

    mapping(address => uint256) public partnerIndexes;

    address owner;

    constructor(address trustedForwarder) ERC2771Context(address(trustedForwarder)) {
        owner = _msgSender();

        _grantRole(DEFAULT_ADMIN_ROLE, owner);
        _grantRole(ADMIN_ROLE, owner);
        _grantRole(MANAGER_ROLE, owner);

        addPartner(owner, 0);

        changeFee(Service.MINT, 0);
        changeFee(Service.BURN, 0);
        changeFee(Service.TRANSFER, 0);
        changeFee(Service.FOREX, 0);
        changeFee(Service.DEX, 0);
        changeFee(Service.LEND, 0);

    }

    function _msgSender() internal view override(ERC2771Context, Context) returns (address sender) {
        return ERC2771Context._msgSender();
    }

    function _msgData() internal view override(Context, ERC2771Context) returns (bytes calldata) {
        return super._msgData();
    }

    modifier onlyManager() {
        require(hasRole(MANAGER_ROLE, _msgSender()), "FeeManager: must have manager role");
        _;
    }

    modifier onlyOwner() {
        require(hasRole(ADMIN_ROLE, _msgSender()), "FeeManager: must have owner role");
        _;
    }

    function addOwner(address newOwner) public onlyOwner {
         require(newOwner != address(0), "Invalid newOwner address");
        owner = newOwner;
    }

   function grantManagerRole(address account) public onlyOwner {
        // Only the address with the "ADMIN_ROLE" can grant the "MANAGER_ROLE"
        _grantRole(MANAGER_ROLE, account);
    }

    function changeFee(Service service, uint256 value ) public onlyOwner {
        require(value >= 0 && value <= 10000000, "FeeManager: percent must be between 0 and 10%");

        fees[service] = value; 
    }

    // Create
    function addPartner(address _wallet, uint256 _fee) public onlyOwner{
        require(_wallet != address(0), "Invalid wallet address");
        require(_fee >= 0, "Fee must be greater than 0");

        uint256 index = partnerIndexes[_wallet];
        if (index == 0) {
            partnersFees.push(PartnersFee({wallet: _wallet, fee: _fee}));
            partnerIndexes[_wallet] = partnersFees.length;
        } else {
            partnersFees[index - 1].fee = _fee;
        }
    }
    // Update
    function updatePartnerFee(address _wallet, uint256 _newFee) public onlyOwner{
        uint256 index = partnerIndexes[_wallet];
        require(index > 0, "Partner not found");
        partnersFees[index - 1].fee = _newFee;
    }

    // Delete
    function removePartner(address _wallet) public onlyOwner {
        uint256 index = partnerIndexes[_wallet];
        require(index > 0, "Partner not found");

        // If the partner to be removed is the last element, just pop the array
        if (index == partnersFees.length) {
            partnersFees.pop();
        } 
        // Otherwise, move the last partner to the index of the partner to remove
        else {
            partnersFees[index - 1] = partnersFees[partnersFees.length - 1];
            partnersFees.pop();
        }

        // Update the index of the last partner
        if (partnersFees.length > 0) {
            partnerIndexes[partnersFees[partnersFees.length - 1].wallet] = index;
        }

        // Reset the index of the removed partner
        delete partnerIndexes[_wallet];
    }

    // Read
    function getPartner(uint256 _index) public view returns (PartnersFee memory) {
        require(_index < partnersFees.length, "Index out of bounds");
        return partnersFees[_index];
    }

    function getPartnerByWallet(address _wallet) public view returns (PartnersFee memory) {
        uint256 index = partnerIndexes[_wallet];
        require(index > 0, "Partner not found");
        return partnersFees[index - 1];
    }

    // Get all partners
    function getAllPartners() public view returns (PartnersFee[] memory) {
        return partnersFees;
    }


    // function takeFee(address _partner, uint256 amount, Service service) public view returns (uint256 ) {

    //     require(amount > 0, "Amount must be greater than 0");

    //     PartnersFee memory partner;
    //     uint256 index = partnerIndexes[_partner];

    //     if (index > 0)
    //         partner = partnersFees[index - 1];
    //     else 
    //         partner = getPartnerByWallet(owner);

    //     uint256 finalAmount =0;
    //     //FeePartner

    //    finalAmount = amount - ( ((amount * partner.fee) + (amount * fees[service] )  )/ 100000000);
    //     //FeeService
    // //    amount  = amount - ( (amount * fees[service]) / 100000000);
    //     // uint256  ammm = amount - (amount_p+amount_s);
    //     return  amount;
    // }


    function calculatePartnerAmount(address _partner, uint256 amount) public view returns (uint256 ) {
        require(amount >= 0, "Amount must be greater than 0");

        PartnersFee memory partner;
        uint256 index = partnerIndexes[_partner];


        if (index > 0)
            partner = partnersFees[index - 1];
        else 
            partner = getPartnerByWallet(owner);


        uint256 pAmount =0;

         //FeePartner
        if(partner.fee>0)
         pAmount = (amount * partner.fee) / 100000000;

        return  pAmount;
    }


    function calculateFeeAmount(Service service, uint256 amount) public view returns (uint256 ) {
        require(amount >= 0 , "Amount must be greater than 0");
        //FeePartner

        uint256 fAmount =0;

        if(fees[service]>0)
         fAmount= (amount * fees[service]) / 100000000;

        return  fAmount;
    }


}

contracts/StableManager.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./ERC20Stable.sol";
import "./FeeManager.sol";


import {
    ERC2771Context
} from "@gelatonetwork/relay-context/contracts/vendor/ERC2771Context.sol";




contract StableManager is  ERC2771Context, AccessControl, Pausable {


    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    using Math for uint256;

    address public feeCollector;



    IERC20 public USDToken;

    FeeManager public feeManager;

    struct Stable {
        ERC20Stable   tokenAddress;
        uint          price;
        uint256       tokenReserve;
        bool          tradable;
    }

    enum Operation {
        ADD,
        SUB
    }


    mapping(address => Stable) public stables;


    uint priceFloatDigit   = 10**6;
    uint percentFloatDigit = 10**2;
    uint percentMax        = 100**2;


    constructor(address trustedForwarder) ERC2771Context(address(trustedForwarder)) { 
        // owner = _msgSender();
        feeCollector = _msgSender();

        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(ADMIN_ROLE, _msgSender());
        _grantRole(MANAGER_ROLE, _msgSender());

       USDToken = ERC20(0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48);

    }  

    modifier onlyManager() {
        require(hasRole(MANAGER_ROLE, _msgSender()), "Must have manager role");
        _;
    }

    modifier onlyOwner() {
        require(hasRole(ADMIN_ROLE, _msgSender()), "Must have ADMIN_ROLE role");
        _;
    }



    function grantNewRole(bytes32 role, address account) public onlyOwner{
        // Only the address with the "ADMIN_ROLE" can grant the "MANAGER_ROLE"
        _grantRole(role, account);
    }

    function revoveRole(bytes32 role, address account) public onlyOwner {
        // Only the address with the "ADMIN_ROLE" can grant the "MANAGER_ROLE"
        _revokeRole(role, account);
    }




    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }




    function changeFeeManager(address _feeManager) public onlyOwner  returns (bool) {
        require(_feeManager != address(0), "feeManager can't be null");
        feeManager = FeeManager(_feeManager);
        return true;
    }

    function changeFeeCollector(address _feeCollector) public onlyOwner  returns (bool) {
        require(_feeCollector != address(0), "feeCollector can't be null");
        feeCollector = _feeCollector;
        return true;
    }


    function changeUSD(address usdToken) public onlyOwner  returns (bool) {
        require(usdToken != address(0), "Stable1 doest not exist");
        USDToken = ERC20(usdToken);
        return true;
    }



    function _msgSender() internal view override(ERC2771Context, Context) returns (address sender) {
        return ERC2771Context._msgSender();
    }

    function _msgData() internal view override(Context, ERC2771Context) returns (bytes calldata) {
        return super._msgData();
    }


    // =================================================Stables Coin Management====================================================


    function addStable(ERC20Stable tokenAddress, uint priceInit, uint256  tokenReserve, bool tradable) onlyOwner public returns (Stable memory) {

        Stable memory stb = stables[address(tokenAddress)];

        require(stb.tokenAddress == ERC20Stable (address(0)) , "StableManager: token address  extists");
        require(address(tokenAddress) != address(0), "StableManager: token address cannot be zero");
        require(priceInit > 0, "StableManager: price must be greater than zero");
        stables[address(tokenAddress)] = Stable(tokenAddress, priceInit, tokenReserve, tradable);

        return stables[address(tokenAddress)];
    }

    function removeStableByAddress(ERC20Stable tokenAddress) onlyOwner public returns (Stable memory) {


        require(address(tokenAddress) != address(0), "StableManager: token address cannot be zero");
        delete stables[address(tokenAddress)];

        return stables[address(tokenAddress)];
    }

    function getStable(address tokenAddress) public view returns ( Stable memory) {
        return stables[address(tokenAddress)];
    }

    function updateStableByAddress(ERC20Stable tokenAddress, uint price, uint256  tokenReserve, bool tradable) onlyOwner public returns (Stable memory)  {

        require(address(tokenAddress) != address(0), "StableManager: token address cannot be zero");
        require(price > 0, "StableManager: price must be greater than zero");
        stables[address(tokenAddress)] = Stable(tokenAddress, price, tokenReserve, tradable);

        return stables[address(tokenAddress)];

    }

    function updateStablePrice(ERC20Stable tokenAddress, uint newPrice) public onlyOwner returns (bool) {
        require(newPrice > 0, "StableManager: price must be greater than zero");
        stables[address(tokenAddress)].price = newPrice;
        return false;
    }

    function updateStableReserve(ERC20Stable tokenAddress, uint  amount, Operation  operation) internal  returns (bool)   {

        require(address(tokenAddress) != address(0), "StableManager: token address cannot be zero");

        if (operation == Operation.ADD) {
            stables[address(tokenAddress)].tokenReserve += amount;
        } else if (operation == Operation.ADD) {
            stables[address(tokenAddress)].tokenReserve -= amount;
        }
        return false;

    }

    // =================================================Stables Converter===================================

    function stableTrade(address _stable1, address _stable2, uint256 amount, address partner) public  whenNotPaused returns (uint256) {

        FeeManager.PartnersFee memory partnerFee = feeManager.getPartnerByWallet(partner);

        //Initialize ERC20 token
        ERC20Stable stableCoin1 = ERC20Stable(_stable1);
        ERC20Stable stableCoin2 = ERC20Stable(_stable2);

        //Get stable information
        Stable memory stable1 = getStable(_stable1);
        Stable memory stable2 = getStable(_stable2);


        // uint256 nAmount = feeManager.takeFee(partnerFee.wallet, amount, FeeManager.Service.FOREX);
        uint256 pAmount = feeManager.calculatePartnerAmount(partnerFee.wallet, amount);
        uint256 fAmount = feeManager.calculateFeeAmount(FeeManager.Service.FOREX, amount);

        uint256 nAmount = amount - (pAmount + fAmount);

        //Get sender balance
        uint senderBalance = stableCoin1.balanceOf(_msgSender());


        //Check amount, Balance, allowance, token price, existence
        require(_stable1 != address(0), "Stable1 doest not exist");
        require(_stable2 != address(0), "Stable2 doest not exist");
        require(amount > 0, "Amount can't be zero");
        require(senderBalance >= amount, "Token not enough");
        require(stableCoin1.allowance(_msgSender(), address(this)) >= amount, "Allowance not enough");
        require(stable1.price > 0, "Stable1 Price has not been define");
        require(stable2.price > 0, "Stable2 Price has not been define");

        //Fist step convert Stable1 to USD
        uint256 usd =  (nAmount * priceFloatDigit ) / stable1.price;

        //Smart contract Burn those tokens  
        stableCoin1.burnFrom(_msgSender(), amount);

        //Transfer fund for partner fee
        stableCoin2.mint(partnerFee.wallet, pAmount);
        //Transfer fund for service fee
        stableCoin2.mint(feeCollector, fAmount);

        //Decrease usd reserve allocate to  Stable1
        updateStableReserve(stable1.tokenAddress, usd, Operation.SUB);

        //Second step convert USD to Stable2
        uint256 tokens = (usd * stable2.price) / priceFloatDigit;

        //Smart contract Mint the Stable2
        stableCoin2.mint(_msgSender(), tokens);

        //Update reserve allocate to  Stable2
        updateStableReserve(stable2.tokenAddress, usd, Operation.ADD);



        //Operation successful
        return nAmount;
    }

    function mintStable(address tokenAddress, uint256 usdAmount, address partner) public  whenNotPaused returns (uint256) {

        FeeManager.PartnersFee memory partnerFee = feeManager.getPartnerByWallet(partner);

        //Initialize ERC20 token
        ERC20Stable stableCoin = ERC20Stable(tokenAddress);


        //Get stable information
        Stable memory stable = getStable(tokenAddress);


        //Check amount USD, Balance, allowance, token price
        require(usdAmount > 0, "Usd amount can't be zero");
        require(USDToken.balanceOf(_msgSender()) >= usdAmount, "USD Token not enough");
        require(USDToken.allowance(_msgSender(), address(this)) >= usdAmount, "USD Token Allowance not enough");
        require(stable.price > 0, "Price has not been defined");

        //Get token amount to send after tax
        uint256 amount = (usdAmount * stable.price) / priceFloatDigit;

        //Tranfer USD from the sender to the smart contract
        USDToken.transferFrom(_msgSender(), address(this), usdAmount);

        //Calculate amount
        // uint256 nAmount = feeManager.takeFee(partnerFee.wallet, amount, FeeManager.Service.MINT);
        uint256 pAmount = feeManager.calculatePartnerAmount(partnerFee.wallet, amount);
        uint256 fAmount = feeManager.calculateFeeAmount(FeeManager.Service.MINT, amount);
        uint256 nAmount = amount - (pAmount+ fAmount);
        //Update reserve allocate to this stable
        updateStableReserve(stableCoin, usdAmount, Operation.ADD);

        //Transfer fund to sender
        stableCoin.mint(_msgSender(), nAmount);

        //Transfer partner fee
        stableCoin.mint(partnerFee.wallet, pAmount);
        //Transfer service fee
        stableCoin.mint(feeCollector, fAmount);

        return nAmount;
    }

    function burnStable(address tokenAddress, uint256 amount, address partner) public whenNotPaused returns (uint256) {
        FeeManager.PartnersFee memory partnerFee = feeManager.getPartnerByWallet(partner);

        //Initialize ERC20 token
        ERC20Stable stableCoin = ERC20Stable(tokenAddress);

        //Get stable information
        Stable memory stable = getStable(tokenAddress);


        // uint256 nAmount = feeManager.takeFee(partnerFee.wallet, amount, FeeManager.Service.BURN);
        uint256 pAmount = feeManager.calculatePartnerAmount(partnerFee.wallet, amount);
        uint256 fAmount = feeManager.calculateFeeAmount(FeeManager.Service.BURN, amount);
        uint256 nAmount = amount - (pAmount+ fAmount);
        //Check amount USD, Balance, allowance, token price
        require(amount > 0, "Amount need to be greater than zero");
        require(stableCoin.balanceOf(_msgSender()) >= amount, "Token not enough");
        require(stableCoin.allowance(_msgSender(), address(this)) >= amount, "Allowance not enough");
        require(stable.price > 0, "Price has not been define");

        // uint256 usd = mul(div(tokenAmount, stable.price), priceFloatDigit);
        uint256 usd = (nAmount * priceFloatDigit)/ stable.price;

        //Burn those tokens         
        stableCoin.burnFrom(_msgSender(), amount);

        //Update reserve allocate to this stable
        updateStableReserve(stableCoin, usd, Operation.SUB);

        //Transfer USD to the sender
        USDToken.transfer(_msgSender(), usd);
        //Transfer partner fee
        stableCoin.mint(partnerFee.wallet, pAmount);
        //Transfer service fee
        stableCoin.mint(feeCollector, fAmount);

        return usd;
    }

    function sendStable(address ercAddress, address to,  uint256 amount, address partner) public whenNotPaused returns (uint256) {

        FeeManager.PartnersFee memory partnerFee = feeManager.getPartnerByWallet(partner);

        require(amount > 0, "Tokens amount can't be zero");
        require(ercAddress != address(0), "ERC20 can't be null address");
        require(to != address(0), "Recipient can't be null address");

        // uint256 nAmount = feeManager.takeFee(partnerFee.wallet, amount, FeeManager.Service.BURN);
        uint256 pAmount = feeManager.calculatePartnerAmount(partnerFee.wallet, amount);
        uint256 fAmount = feeManager.calculateFeeAmount(FeeManager.Service.BURN, amount);
        uint256 nAmount = amount - (pAmount+ fAmount);
        ERC20 ErcToken  = ERC20(ercAddress);
        require(ErcToken.balanceOf(_msgSender()) >= amount, "Token not enough");

        ErcToken.transferFrom(_msgSender(), to , nAmount);
        ErcToken.transferFrom(_msgSender(), partnerFee.wallet , pAmount);
        ErcToken.transferFrom(_msgSender(), feeCollector , fAmount);

        return nAmount;
    }



    // // ================================================= Reserve Manager ===================================

    function getUSDReserve() public view  returns(uint256){
            return USDToken.balanceOf(address(this));
    }

    function emergencyTransferReseve(address recipient) public onlyOwner  returns(bool){
        uint256 balance = USDToken.balanceOf(address(this));
        USDToken.transfer(recipient, balance);
        return true;
    }


   }

